import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Logout() {
    const navigate = useNavigate();

    useEffect(() => {
        // Show confirmation dialog
        const confirmLogout = window.confirm("Are you sure you want to log out?");

        if (confirmLogout) {
            // If user confirms logout
            // Simulate logout by clearing session/local storage or any other logout logic
            localStorage.removeItem('user');  // Example for clearing user data
            sessionStorage.removeItem('user'); // If you store user data in sessionStorage

            // Redirect to the admin login page after 5 seconds
            const timer = setTimeout(() => {
                navigate('/admin-login');  // Redirect after confirmation
            }, 5000);

            // Cleanup the timer when the component is unmounted
            return () => clearTimeout(timer);
        } else {
            // If user cancels logout, they are redirected back to the previous page or home
            navigate(-1);  // Navigate back to the previous page or replace this with a different redirect
        }
    }, [navigate]);

    return (
        <div className="logout-container">
            <h2>You have been logged out!</h2>
            <p>Redirecting to admin login page...</p>
        </div>
    );
}

export default Logout;
