function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    useEffect(() => {
        // Check if the user is authenticated (example using localStorage)
        const user = localStorage.getItem('user');
        setIsAuthenticated(!!user);
    }, []);

    return (
        <Router>
            <div className="main-container">
                {isAuthenticated && (
                    <header className="header">
                        <div className="header-container">
                            <img src={logo} alt="logo" className="logo" />
                            <h1 className="system-title">Gate Entry Management System</h1>
                        </div>
                    </header>
                )}
                {isAuthenticated && <Navigation />}
                <div className="content-container">
                    <Routes>
                        {!isAuthenticated && <Route path="*" element={<Navigate to="/admin-login" />} />}
                        <Route path="/admin-login" element={<AdminLogin onLogin={() => setIsAuthenticated(true)} />} />
                        {isAuthenticated && (
                            <>
                                <Route path="/" element={<Home />} />
                                <Route path="/entry-exit" element={<EntryExit />} />
                                <Route path="/profile" element={<Profile />} />
                                <Route path="/logout" element={<Logout onLogout={() => setIsAuthenticated(false)} />} />
                                <Route path="/student" element={<Student />} />
                                <Route path="/faculty" element={<Faculty />} />
                                <Route path="/visitor" element={<Visitor />} />
                                <Route path="/support" element={<Support />} />
                            </>
                        )}
                    </Routes>
                </div>
            </div>
        </Router>
    );
}

export default App;
