import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Logout() {
    const navigate = useNavigate();

    // Function to handle the logout confirmation and redirection
    const handleLogout = () => {
        const confirmLogout = window.confirm("Are you sure you want to log out?");

        if (confirmLogout) {
            // Simulate logout by clearing localStorage or sessionStorage
            localStorage.removeItem('user');
            sessionStorage.removeItem('user');

            // Show a message to the user while redirecting
            alert("You have been logged out!");

            // Redirect to the admin login page after 5 seconds
            setTimeout(() => {
                navigate('/admin-login');
            }, 5000);
        } else {
            // If user cancels, redirect them to home or another page
            alert("Logout cancelled.");
            navigate('/');  // Redirect to homepage or previous page
        }
    };

    useEffect(() => {
        handleLogout();
    }, [navigate]); // This ensures that the function is triggered once when the component is loaded.

    return (
        <div className="logout-container">
            <h2>Logging out...</h2>
            <p>You will be redirected to the admin login page shortly.</p>
        </div>
    );
}

export default Logout;
