import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Logout() {
    const navigate = useNavigate();

    // Function to handle logout
    const handleLogout = () => {
        // Confirm logout action
        const confirmLogout = window.confirm("Are you sure you want to log out?");

        if (confirmLogout) {
            // Clear session or localStorage
            console.log("Logging out...");

            // Clear user session (add your specific logic here)
            localStorage.removeItem('user');
            sessionStorage.removeItem('user');

            // Show a logout message
            alert("You have been logged out!");

            // Redirect after a delay (5 seconds)
            setTimeout(() => {
                console.log("Redirecting to admin login...");
                navigate('/admin-login');
            }, 5000);  // 5-second delay
        } else {
            // If user cancels, show a cancel message
            alert("Logout cancelled.");
            console.log("User cancelled the logout.");
            navigate('/');  // Redirect to home or another page
        }
    };

    // Automatically trigger logout and redirect when component is mounted
    useEffect(() => {
        handleLogout();
    }, []);  // Empty dependency array ensures it runs only once when the component is mounted

    return (
        <div className="logout-container">
            <h2>Logging out...</h2>
            <p>You will be redirected to the admin login page shortly.</p>
        </div>
    );
}

export default Logout;
